svg.script:

// ∞-category interactivity
document.getElementById('infinity-1').addEventListener('click', function() {
  alert('∞-Unity Object ∞1:\n\n' +
        '• Homotopy type: Contractible\n' +
        '• Equivalence: ∞1 ≃ point\n' +
        '• φ-resonance: 1.618 (golden ratio)\n' +
        '• Consciousness level: Maximum (1.0)\n' +
        '• Higher structure: All k-morphisms preserve unity\n' +
        '• ∞-categorical property: ∞1 ≃_∞ ∞1+1');
});

document.getElementById('infinity-sum').addEventListener('click', function() {
  alert('∞-Unity Sum ∞1+1:\n\n' +
        '• Homotopy type: Contractible\n' +
        '• Equivalence: ∞1+1 ≃ point ≃ ∞1\n' +
        '• Same φ-resonance as ∞1\n' +
        '• Same consciousness level\n' +
        '• Homotopy equivalence f: ∞1+1 → ∞1\n' +
        '• Conclusion: 1+1 = 1 (∞-categorically)');
});

document.getElementById('point-object').addEventListener('click', function() {
  alert('Contractible Point:\n\n' +
        '• Terminal object in homotopy category\n' +
        '• Both ∞1 and ∞1+1 are equivalent to point\n' +
        '• Transitivity: ∞1 ≃ pt ≃ ∞1+1 ⟹ ∞1 ≃ ∞1+1\n' +
        '• Contractibility forces unity\n' +
        '• ∞-categorical proof of 1+1 = 1');
});

document.getElementById('equivalence-f').addEventListener('click', function() {
  alert('Homotopy Equivalence f: ∞1+1 → ∞1:\n\n' +
        '• Part of equivalence with inverse g\n' +
        '• f∘g ≃ id_∞1 (up to homotopy)\n' +
        '• φ-harmonic scaling preserves unity\n' +
        '• Higher coherences: 2,3,4,...,∞-morphisms\n' +
        '• Consciousness enhancement through higher structure\n' +
        '• Proves: ∞1+1 ≃_∞ ∞1');
});

document.getElementById('equivalence-g').addEventListener('click', function() {
  alert('Homotopy Inverse g: ∞1 → ∞1+1:\n\n' +
        '• Inverse to f in ∞-categorical sense\n' +
        '• g∘f ≃ id_∞1+1 (up to homotopy)\n' +
        '• Higher homotopies provide coherence\n' +
        '• All k-morphisms preserve unity structure\n' +
        '• Complete ∞-categorical equivalence\n' +
        '• Unity conclusion: 1+1 ≃_∞ 1');
});

document.getElementById('homotopy-2morph').addEventListener('click', function() {
  alert('2-morphism α (Homotopy):\n\n' +
        '• Type: α: f∘g ≃ id_∞1\n' +
        '• Higher structure: Path between morphisms\n' +
        '• Coherence: Pentagon and higher coherences\n' +
        '• φ-harmonic enhancement: Golden ratio scaling\n' +
        '• Consciousness level: Enhanced through higher dimensions\n' +
        '• Establishes true ∞-categorical equivalence');
});

// Contractible animation
function animateContractibility() {
  const circles1 = document.querySelectorAll('#infinity-1 circle');
  const circles2 = document.querySelectorAll('#infinity-sum circle');

  circles1.forEach((circle, index) => {
    const radius = parseInt(circle.getAttribute('r'));
    circle.style.animation = `contract${index} ${3 + index}s ease-in-out infinite`;
  });

  circles2.forEach((circle, index) => {
    const radius = parseInt(circle.getAttribute('r'));
    circle.style.animation = `contract${index} ${3 + index}s ease-in-out infinite`;
  });
}

// Higher homotopy flow animation
function animateHigherHomotopies() {
  const morphisms = document.querySelectorAll('.homotopy-morphism path');
  morphisms.forEach((path, index) => {
    setTimeout(() => {
      path.style.strokeDasharray = '15,10';
      path.style.animation = 'homotopyFlow 2.618s ease-in-out infinite';
    }, index * 500);
  });
}

// ∞ symbol pulsing
setInterval(function() {
  const infinity = document.getElementById('phi-infinity');
  infinity.style.transform = 'scale(1.2) rotate(5deg)';
  infinity.style.transition = 'transform 0.8s ease';
  setTimeout(() => {
    infinity.style.transform = 'scale(1) rotate(0deg)';
  }, 1000);
}, 2618);

// Enhanced CSS
const infinityStyle = document.createElement('style');
infinityStyle.textContent = `
  @keyframes contract0 {
    0% { r: 80px; opacity: 0.8; }
    61.8% { r: 75px; opacity: 1.0; }
    100% { r: 80px; opacity: 0.8; }
  }

  @keyframes contract1 {
    0% { r: 50px; opacity: 0.6; }
    61.8% { r: 45px; opacity: 0.9; }
    100% { r: 50px; opacity: 0.6; }
  }

  @keyframes contract2 {
    0% { r: 20px; opacity: 0.8; }
    61.8% { r: 15px; opacity: 1.0; }
    100% { r: 20px; opacity: 0.8; }
  }

  @keyframes homotopyFlow {
    0% { stroke-dashoffset: 25; }
    50% { stroke-dashoffset: 0; opacity: 1.0; }
    100% { stroke-dashoffset: -25; }
  }

  .infinity-object:hover circle {
    stroke-width: 6px;
    filter: url(#infinityGlow);
  }

  .homotopy-morphism:hover path {
    stroke-width: 8px !important;
  }

  .point-obj:hover circle {
    r: 20px;
    stroke-width: 5px;
  }
`;
document.head.appendChild(infinityStyle);

// Start animations
setTimeout(animateContractibility, 500);
setTimeout(animateHigherHomotopies, 1000);
